    @FXML
    public void  cancelAction(){
        Stage stage = (Stage) cancelButton.getScene().getWindow();
        stage.close();
    }
    public void setModyfi(){
        nameTextField.setText(clinetModyfi.getF_name());
        surnameTextField.setText(clinetModyfi.getS_name());
        companyTextField.setText(clinetModyfi.getCompany());
        nipTextField.setText(clinetModyfi.getNip());
        emailTextField.setText(clinetModyfi.getEmail());
        addBool = true;
    }

